import './Map.css';
import { MapContainer, TileLayer } from 'react-leaflet'
import MarkerPoint from './MarkerPoint.js'
import { useEffect, useState } from 'react';
import TimeRangeSlider from './TimeRangeSlider';

function Map() {

  const START = "2022-01-01"
  const END = "2022-06-01"

  const [markers, setMarkers] = useState([])
  const [range, setRange] = useState([new Date(START).getTime(),new Date(END).getTime()])

  const maxBounds = [
    [69.89, -32.58],
    [59.89, -4.58]
  ]

  useEffect(() => {
    fetch("http://localhost:3001/api/v1/points")
    .then(res => res.json())
    .then((res) => setMarkers(res))
  }, [])

  const dateRangeChanged = (rangeReceived) => {
    setRange(rangeReceived)
  }

  const markersComponents = markers.map((marker, key) => {
    if(new Date(marker.date).getTime() > range[0] && marker.date < range[1]) {
      return <MarkerPoint id={marker.id} lat={marker.lat} lng={marker.lng} date={marker.date} notes={marker.notes} filename={marker.filename} />
    }
  })


  return (
    <div>
      <TimeRangeSlider min={START} max={END} dateRangeChanged={dateRangeChanged} />
      <MapContainer center={[64.89, -18.58]} zoom={7} scrollWheelZoom={true} minZoom={6} maxBounds={maxBounds} id="map">
          <TileLayer
              attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />


          {markersComponents}
      </MapContainer>
    </div>
  );
}

export default Map;
