import { useEffect, useState } from 'react';
import './TagList.css'

function TagList(props) {

    const [checkedState, setCheckedState] = useState(new Array(props.tags.length).fill(true))

    useEffect(() => {
        if(checkedState.length === 0) {
            setCheckedState(new Array(props.tags.length).fill(true))
        }
    }, [props.tags])

    const handleOnChange = (position) => {
        const newCheckedState = checkedState.map((tag, index) =>
            index == position ? !tag : tag
        )

        setCheckedState(newCheckedState)

        let listChecked = []
        for(let i = 0; i < props.tags.length; i++) {
            if(checkedState[i]) {
                listChecked.push(props.tags[i])
            }
        }
        props.checkedChanged(listChecked)
    }

  return (
    <div>
        <p>Catégories à afficher</p>
        <ul>
            {props.tags.map((tag, index) => {
                if(checkedState.length > 0)
                return (
                    <li key={index}>
                        <div>
                            <input type="checkbox" name={tag} value={tag} checked={checkedState[index]} onChange={() => handleOnChange(index)} />
                            <label>{tag}</label>
                        </div>
                    </li>
                )
            })}
        </ul>
    </div>
  );
}

export default TagList;
